// pages/HomePage.js
import React, { useState, useContext, useEffect } from 'react';
import { Container, Row, Col, Button } from 'react-bootstrap';
import TodayLog from './home/TodayLog';
import Statistics from './home/Statistics';
import { useNavigate } from 'react-router-dom';
import TokenContext from './TokenContext';

const HomePage = () => {
    const { getNutritionLogByDate, getFoodByDate } = useContext(TokenContext);
    const [foodEntries, setFoodEntries] = useState([
        { name: "Breakfast", calories: 300 },
        { name: "Lunch", calories: 600 },
        { name: "Dinner", calories: 500 },
    ]);

    const [todaysNutritionLog, setTodaysnutritionLog] = useState();
    const [todaysFoodLog, setTodaysFoodLog] =useState();

    const navigate = useNavigate();

    useEffect(() => {
        const fetchLogData = async () => {
            const response = await getNutritionLogByDate(new Date().toISOString().split('T')[0]);
            if(response!==null)
            setTodaysnutritionLog(response.data)
        };

        fetchLogData();
    }, []);

    useEffect(() => {
        const fetchFoodLogData = async () => {
            const response = await getFoodByDate(new Date().toISOString().split('T')[0]);
            if(response!==null)
                setTodaysFoodLog(response.data)
        };

        fetchFoodLogData();
    }, []);

    const navigateToAddFood = () => {
        navigate('/search');
    };

    const handleAddFood = (food) => {
        setFoodEntries([...foodEntries, food]);
    };

    return (
        <Container>
            <Row>
                <Col md={8}>
                    <TodayLog foodEntries={foodEntries} onAddFood={navigateToAddFood} />
                </Col>
                <Col md={4}>
                    {todaysNutritionLog ? (
                        <Statistics nutrients={todaysNutritionLog} />
                    ) : (
                        <p>Loading...</p>
                    )}
                </Col>
            </Row>
        </Container>
    );
};

export default HomePage;
