import React from 'react';
import { Card, ListGroup } from 'react-bootstrap';
import { GiFruitBowl } from 'react-icons/gi'; // Example icon for nutrients

const Statistics = ({ nutrients }) => {
    return (
        <Card className="mb-4" style={{ backgroundColor: '#f3f3f3', boxShadow: '0 4px 8px rgba(0,0,0,0.1)', border: '1px solid #ddd' }}>
            <Card.Header style={{ backgroundColor: '#64b064', color: 'white', fontWeight: 'bold' }}>Statistics</Card.Header>
            <Card.Body>
                <ListGroup variant="flush">
                    {nutrients.map((nutrient, index) => (
                        <ListGroup.Item key={index} style={{ padding: '12px', borderBottom: '1px solid #ddd', display: 'flex', alignItems: 'center' }}>
                            <GiFruitBowl size={24} style={{ marginRight: '10px', color: '#64b064' }} />
                            <div style={{ flex: 1 }}>
                                <strong>{nutrient.nutrientName}</strong>: {nutrient.totalAmount} {nutrient.nutrientUnitName} ({nutrient.totalPercentDailyValue}% DV)
                            </div>
                        </ListGroup.Item>
                    ))}
                </ListGroup>
            </Card.Body>
        </Card>
    );
};

export default Statistics;
