import React, { useState } from 'react';
import { Container, Button, Spinner, Alert, FormControl, InputGroup } from 'react-bootstrap';
import axios from 'axios';
import SearchResults from './SearchResults';  // Ensure the path is correct
import FoodDetails from './FoodDetails';  // Ensure the path is correct

const Search = ({ onAddFood }) => {
    const [searchResults, setSearchResults] = useState([]);
    const [selectedFood, setSelectedFood] = useState(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [searchTerm, setSearchTerm] = useState('');

    const handleSearch = async (searchTerm) => {
        if(searchTerm>2)
            {
        setLoading(true);
        setError('');
        try {
            const response = await axios.get(`http://localhost:8080/api/foods/search?query=${searchTerm}`);
            setSearchResults(response.data);
        } catch (err) {
            setError('Failed to fetch search results. Please try again.');
        } finally {
            setLoading(false);
        }
    }
    else
    {
        setError('Please enter atleast 3 characters')
    }
    };

    const handleSelectResult = async (food) => {
        setLoading(true);
        setError('');
        try {
            const response = await axios.get(`http://localhost:8080/api/foods/searchFoodNutrientById?fdc_id=${food.fdc_id}`);
            setSelectedFood(response.data);
        } catch (err) {
            setError('Failed to fetch food details. Please try again.');
        } finally {
            setLoading(false);
        }
    };

    const handleAddFood = (food) => {
        onAddFood(food);
        setSelectedFood(null);
    };

    return (
        <Container>
            <h3>Search Food</h3>
            <InputGroup className="mb-3">
                <FormControl
                  placeholder="Search for food"
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                />
                <InputGroup.Append>
                    <Button variant="primary" onClick={handleSearch}>Search</Button>
                </InputGroup.Append>
             </InputGroup>
            {loading && <Spinner animation="border" />}
            {error && <Alert variant="danger">{error}</Alert>}
            {searchResults.length > 0 && (
                <SearchResults results={searchResults} onSelect={handleSelectResult} />
            )}
            {selectedFood && (
                <FoodDetails foodItem={selectedFood} onAdd={handleAddFood} />
            )}
            <Button variant="secondary" onClick={() => window.history.back()}>Back</Button>
        </Container>
    );
};

export default Search;
