import React, { useState } from 'react';
import { Card, Table, Button, ButtonGroup } from 'react-bootstrap';
import { BsArrowLeft, BsArrowRight } from 'react-icons/bs'; // Example icons for date navigation

const TodayLog = ({ foodEntries, onAddFood }) => {
    const [currentDate, setCurrentDate] = useState(new Date());

    const handleUpdate = (index) => {
        // Dummy update function
        alert(`Update functionality for index ${index}`);
    };

    const handleDelete = (index) => {
        // Dummy delete function
        alert(`Delete functionality for index ${index}`);
    };

    const isToday = (date) => {
        const today = new Date();
        return date.getDate() === today.getDate() &&
               date.getMonth() === today.getMonth() &&
               date.getFullYear() === today.getFullYear();
    };

    const handleDateChange = (direction) => {
        const newDate = new Date(currentDate);
        if (direction === 'prev') {
            newDate.setDate(currentDate.getDate() - 1);
        } else if (direction === 'next') {
            newDate.setDate(currentDate.getDate() + 1);
        }
        setCurrentDate(newDate);
    };

    return (
        <Card className="mb-4">
            <Card.Header style={{ backgroundColor: '#e6ffe6', color: '#006400', fontWeight: 'bold', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <div>
                    {currentDate.toDateString() === new Date().toDateString() ? (
                        <span>Today</span>
                    ) : currentDate.getDate() === new Date().getDate() - 1 ? (
                        <span>Yesterday</span>
                    ) : (
                        <span>{currentDate.toDateString()}</span>
                    )}
                </div>
                <ButtonGroup>
                    <Button variant="outline-secondary" onClick={() => handleDateChange('prev')} disabled={isToday(currentDate)}>
                        <BsArrowLeft />
                    </Button>
                    <Button variant="outline-secondary" onClick={() => handleDateChange('next')}>
                        <BsArrowRight />
                    </Button>
                </ButtonGroup>
            </Card.Header>
            <Card.Body>
                <Table striped bordered hover responsive>
                    <thead>
                        <tr>
                            <th style={{ minWidth: '200px' }}>Food Name</th>
                            <th style={{ minWidth: '150px' }}>Serving Size</th>
                            <th style={{ minWidth: '150px' }}>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {foodEntries.map((entry, index) => (
                            <tr key={index}>
                                <td>{entry.foodName}</td>
                                <td>{entry.servingSize}</td>
                                <td>
                                    <Button variant="outline-primary" size="sm" className="me-2" onClick={() => handleUpdate(index)}>
                                        Update
                                    </Button>
                                    <Button variant="outline-danger" size="sm" onClick={() => handleDelete(index)}>
                                        Delete
                                    </Button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </Table>
                <Button variant="primary" onClick={onAddFood} className="mt-3">
                    Add Food
                </Button>
            </Card.Body>
        </Card>
    );
};

export default TodayLog;
