import React, { useState, useContext } from 'react';
import { Container, Button, Spinner, Alert, FormControl, InputGroup } from 'react-bootstrap';
import SearchResults from './SearchResults';  // Ensure the path is correct
import FoodDetails from './FoodDetails';  // Ensure the path is correct
import TokenContext from '../TokenContext';

const Search = ({ onAddFood }) => {
    const [searchResults, setSearchResults] = useState([]);
    const [selectedFoodNutrition, setSelectedFoodNutrition] = useState(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [searchTerm, setSearchTerm] = useState('');
    const { searchByFoodName, getFoodNutritionDetails, userId, logFoodNutritionDetails } = useContext(TokenContext);

    const handleSearch = async () => {
        setError('');
        if (searchTerm.length >= 3) {
            setLoading(true);
            try {
                const searchResponse = await searchByFoodName(searchTerm);
                if (searchResponse === 0) {
                    setError('Search Failed');
                } else {
                    setSearchResults(searchResponse.data);
                }
            } catch (err) {
                setError('Search Failed');
            } finally {
                setLoading(false);
            }
        } else {
            setError('Please enter at least 3 characters');
        }
    };

    const handleSelectResult = async (food) => {
        setLoading(true);
        setError('');
        try {
            const foodNutritionDetailsResponse = await getFoodNutritionDetails(food);
            setSelectedFoodNutrition({ name: food.name, nutrients: foodNutritionDetailsResponse.data, fdcId: food.fdcId });
        } catch (err) {
            setError('Failed to fetch food details. Please try again.');
        } finally {
            setLoading(false);
        }
    };

    const handleAddFood = async (servingSize) => {
        try {
            const logEntry = {
                userId: userId, // Assuming a static user ID, replace this with dynamic user ID if available
                foodId: selectedFoodNutrition.fdcId, // Assuming `foodItem` has an `id` field
                date: new Date().toISOString().split('T')[0],
                quantity: parseFloat(servingSize)
            };
            const foodLogAddResponse = await logFoodNutritionDetails(logEntry);
        } catch (err) {
            setError('Failed to Add Log details. Please try again.');
        } finally {
            setLoading(false);
        }
    };

    return (
        <Container>
            <h3>Search Food</h3>
            <InputGroup className="mb-3">
                <FormControl
                    placeholder="Search for food"
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                />
                <InputGroup.Text>
                    <Button variant="primary" onClick={handleSearch}>Search</Button>
                </InputGroup.Text>
            </InputGroup>
            {loading && <Spinner animation="border" />}
            {error && <Alert variant="danger">{error}</Alert>}
            {searchResults.length > 0 && (
                <SearchResults results={searchResults} onSelect={handleSelectResult} />
            )}
            {selectedFoodNutrition && (
                <FoodDetails foodItem={selectedFoodNutrition} onAdd={handleAddFood} />
            )}
            <Button variant="secondary" onClick={() => window.history.back()}>Back</Button>
        </Container>
    );
};

export default Search;
