import React, { useState, useContext, useEffect } from 'react';
import { Container, Row, Col, Card, ButtonGroup, Button } from 'react-bootstrap';
import TodayLog from './home/TodayLog';
import Statistics from './home/Statistics';
import { useNavigate, useLocation } from 'react-router-dom';
import TokenContext from './TokenContext';
import { BsArrowLeft, BsArrowRight } from 'react-icons/bs'; // Example icons for date navigation

const HomePage = () => {
    const { getNutritionLogByDate, getFoodByDate } = useContext(TokenContext);
    const location = useLocation();
    const [foodEntries, setFoodEntries] = useState([]);
    const [message, setMessage] = useState(null);
    const [todaysNutritionLog, setTodaysnutritionLog] = useState([]);
    const[logDate, setLogdate]= useState(new Date())

    const navigate = useNavigate();

    const isToday = (date) => {
        const today = new Date();
        return date.getDate() === today.getDate() &&
               date.getMonth() === today.getMonth() &&
               date.getFullYear() === today.getFullYear();
    };

    useEffect(() => {
        if (location.state && location.state.message) {
          setMessage(location.state.message);
          setTimeout(() => {
            setMessage(null);
          }, 3000);
        }
    }, [location.state]);

    useEffect(() => {
        const fetchLogData = async () => {
            const response = await getNutritionLogByDate(logDate);
            if (response !== null) {
                setTodaysnutritionLog(response.data);
            }
        };

        fetchLogData();
    }, []);

    useEffect(() => {
        const fetchFoodLogData = async () => {
            const response = await getFoodByDate(logDate);
            if (response !== null) {
                setFoodEntries(response.data);
            }
        };

        fetchFoodLogData();
    }, []);

    const navigateToAddFood = () => {
        navigate('/search');
    };

    return (
        <Container>
            {message && <p className="alert alert-success alert-dismissible fade show">{message}</p>}
            <Row>
                <Card>
                <Card.Header style={{ backgroundColor: '#e6ffe6', color: '#006400', fontWeight: 'bold', display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                <ButtonGroup className="mr-2">
                    <Button variant="outline-secondary" onClick={() => {setLogdate(logDate-1)}} >
                        <BsArrowLeft />
                    </Button>
                </ButtonGroup>
                <div style={{ margin: '0 10px' }}>
                    {logDate === new Date()? (
                        <span>Today</span>
                    ) : logDate.getDate() === new Date().getDate() - 1 ? (
                        <span>Yesterday</span>
                    ) : (
                        <span>{logDate}</span>
                    )}
                </div>
                <ButtonGroup className="ml-2">
                    <Button variant="outline-secondary" onClick={() => {setLogdate(logDate+1)}} disabled={isToday(logDate)}>
                        <BsArrowRight />
                    </Button>
                </ButtonGroup>
            </Card.Header>
                </Card>
            </Row>
            <Row>

                <Col md={8}>
                    {foodEntries ? (
                        <TodayLog foodEntries={foodEntries} onAddFood={navigateToAddFood} />
                    ) : (
                        <p>Loading...</p>
                    )}
                </Col>
                <Col md={4}>
                    {todaysNutritionLog ? (
                        <Statistics nutrients={todaysNutritionLog} />
                    ) : (
                        <p>Loading...</p>
                    )}
                </Col>
            </Row>
        </Container>
    );
};

export default HomePage;
