import React, { useState, useContext } from 'react';
import { Container, Button, Spinner, Alert, FormControl, InputGroup } from 'react-bootstrap';
import axios from 'axios';
import SearchResults from './SearchResults';  // Ensure the path is correct
import FoodDetails from './FoodDetails';  // Ensure the path is correct
import TokenContext from '../TokenContext';

const Search = ({ onAddFood }) => {
    const [searchResults, setSearchResults] = useState([]);
    const [selectedFood, setSelectedFood] = useState(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [searchTerm, setSearchTerm] = useState('');
    const { searchByFoodName } = useContext(TokenContext);

    const handleSearch = async () => {
        if (searchTerm.length >= 3) {
            const searchResponse = searchByFoodName(searchTerm)
            if(searchResponse===0)
                {
                    setError('Search Failed')
                }
                else
                {
                    setSearchResults(searchResponse.data)
                }
        } else {
            setError('Please enter at least 3 characters');
        }
    };

    const handleSelectResult = async (food) => {
        setLoading(true);
        setError('');
        try {
            const response = await axios.get(`http://localhost:8080/api/foods/searchFoodNutrientById?fdc_id=${food.fdc_id}`);
            setSelectedFood(response.data);
        } catch (err) {
            setError('Failed to fetch food details. Please try again.');
        } finally {
            setLoading(false);
        }
    };

    const handleAddFood = (food) => {
        onAddFood(food);
        setSelectedFood(null);
    };

    return (
        <Container>
            <h3>Search Food</h3>
            <InputGroup className="mb-3">
                <FormControl
                    placeholder="Search for food"
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                />
                <InputGroup.Text>
                    <Button variant="primary" onClick={handleSearch}>Search</Button>
                </InputGroup.Text>
            </InputGroup>
            {loading && <Spinner animation="border" />}
            {error && <Alert variant="danger">{error}</Alert>}
            {searchResults.length > 0 && (
                <SearchResults results={searchResults} onSelect={handleSelectResult} />
            )}
            {selectedFood && (
                <FoodDetails foodItem={selectedFood} onAdd={handleAddFood} />
            )}
            <Button variant="secondary" onClick={() => window.history.back()}>Back</Button>
        </Container>
    );
};

export default Search;
