import React, { useState, useContext, useEffect } from 'react';
import { Container, Row, Col, Button, Alert } from 'react-bootstrap';
import TodayLog from './home/TodayLog';
import Statistics from './home/Statistics';
import { useNavigate, useLocation } from 'react-router-dom';
import TokenContext from './TokenContext';

const HomePage = () => {
    const { getNutritionLogByDate, getFoodByDate } = useContext(TokenContext);
    const location = useLocation();
    const navigate = useNavigate();

    const [foodEntries, setFoodEntries] = useState([]);
    const [todaysNutritionLog, setTodaysNutritionLog] = useState(null);
    const [message, setMessage] = useState(null);

    // Fetch today's nutrition log and food entries on component mount
    useEffect(() => {
        const currentDate = new Date().toISOString().split('T')[0];

        const fetchLogData = async () => {
            try {
                const response = await getNutritionLogByDate(currentDate);
                if (response && response.data) {
                    setTodaysNutritionLog(response.data);
                }
            } catch (error) {
                console.error('Error fetching nutrition log:', error);
            }
        };

        const fetchFoodLogData = async () => {
            try {
                const response = await getFoodByDate(currentDate);
                if (response && response.data) {
                    setFoodEntries(response.data);
                }
            } catch (error) {
                console.error('Error fetching food entries:', error);
            }
        };

        fetchLogData();
        fetchFoodLogData();
    }, [getNutritionLogByDate, getFoodByDate]);

    // Handle message from location state (e.g., after adding food)
    useEffect(() => {
        if (location.state && location.state.message) {
            setMessage(location.state.message);
            setTimeout(() => {
                setMessage(null);
            }, 3000);
        }
    }, [location.state]);

    // Navigate to the search page to add food
    const navigateToAddFood = () => {
        navigate('/search');
    };

    // Handle adding new food entry
    const handleAddFood = (food) => {
        setFoodEntries([...foodEntries, food]);
    };

    return (
        <Container className="py-4">
            {message && <Alert variant="success">{message}</Alert>}
            <Row>
                <Col md={8}>
                    <TodayLog foodEntries={foodEntries} onAddFood={navigateToAddFood} />
                </Col>
                <Col md={4}>
                    <Statistics nutrients={todaysNutritionLog} />
                </Col>
            </Row>
        </Container>
    );
};

export default HomePage;
