// pages/Search.js
import React, { useState } from 'react';
import { Container, Button } from 'react-bootstrap';
// import SearchBar from '../components/SearchBar';
// import SearchResults from '../components/SearchResults';
// import FoodDetails from '../components/FoodDetails';

const Search = ({ onAddFood }) => {
    const [searchResults, setSearchResults] = useState([]);
    const [selectedFood, setSelectedFood] = useState(null);

    const handleSearch = async (term) => {
        // Simulate a search API call
        const results = await fakeSearchApi(term);
        setSearchResults(results);
    };

    const handleSelectResult = (result) => {
        setSelectedFood(result);
        setSearchResults([]);
    };

    const handleAddFood = (food) => {
        onAddFood(food);
        setSelectedFood(null);
    };

    return (
        <Container>
            <h3>Search Food</h3>
            <SearchBar onSearch={handleSearch} />
            {searchResults.length > 0 && (
                <SearchResults results={searchResults} onSelect={handleSelectResult} />
            )}
            {selectedFood && (
                <FoodDetails foodItem={selectedFood} onAdd={handleAddFood} />
            )}
            <Button variant="secondary" onClick={() => window.history.back()}>Back</Button>
        </Container>
    );
};

// Fake search API function
const fakeSearchApi = async (term) => {
    const allFoods = [
        { name: 'Apple', calories: 95, servingSize: 1, servingType: 'piece' },
        { name: 'Banana', calories: 105, servingSize: 1, servingType: 'piece' },
        { name: 'Chicken Breast', calories: 165, servingSize: 100, servingType: 'g' },
        { name: 'Salad', calories: 150, servingSize: 100, servingType: 'g' },
        // Add more food items for realistic search results
    ];
    return allFoods.filter(food => food.name.toLowerCase().includes(term.toLowerCase()));
};

export default Search;
