import React, { useState, useContext } from 'react';
import TokenContext from './TokenContext'; // Adjust the path as needed
import { Link,useHistory } from 'react-router-dom';

const LoginPage = () => {
    const history = useHistory();
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState("");
  const { isLoggedIn, login } = useContext(TokenContext);

  const handleUsernameChange = (e) => setUsername(e.target.value);
  const handlePasswordChange = (e) => setPassword(e.target.value);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if(username==="" || password===""){
        setErrorMessage("Please enter username and password");
        return;
    }
    const newToken = await login(username, password)
    console.log("token---->",newToken)
    if(newToken===null)
        {
            window.alert("hello error")
            setErrorMessage("Login Failed")
        }
        else
        {
            // window.location.href = '/home';
            history.push('/home');
            window.alert("hello from login")
        }
  };

  return (
    <div className="container mt-5">
        <div>
      {isLoggedIn ? 'Logged In' : 'Not Logged In'}
      <button onClick={() => login('username', 'password')}>Login</button>
    </div>
      <div className="row justify-content-center">
        <div className="col-md-6">
          <div className="card">
            <div className="card-body">
              <h5 className="card-title">Login</h5>
              <form onSubmit={handleSubmit}>
                <div className="form-group mt-5">
                  <label htmlFor="username">Username</label>
                  <input
                    type="text"
                    className="form-control"
                    id="username"
                    value={username}
                    onChange={handleUsernameChange}
                  />
                </div>
                <div className="form-group mt-2">
                  <label htmlFor="password">Password</label>
                  <input
                    type="password"
                    className="form-control"
                    id="password"
                    value={password}
                    onChange={handlePasswordChange}
                  />
                </div>
                <button type="submit" className="btn btn-primary btn-block mt-3">
                  Login
                </button>
                {errorMessage && <p className="text-danger mt-2">{errorMessage}</p>}
              </form>
              <div className="mt-3 text-center">
                <Link to="/signup" className="mx-2">Sign Up</Link>
                <span className="text-muted">|</span>
                <Link to="/reset-password" className="mx-2">Reset Password</Link>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
