import React from 'react';
import { Card, ListGroup, Alert } from 'react-bootstrap';

const Statistics = ({ nutrients }) => {
    if (!nutrients) {
        return (
            <Card className="mb-4">
                <Card.Body>
                    <Alert variant="info">Loading...</Alert>
                </Card.Body>
            </Card>
        );
    }

    return (
        <Card className="mb-4">
            <Card.Header style={{ backgroundColor: '#64b064', color: 'white', fontWeight: 'bold' }}>Statistics</Card.Header>
            <Card.Body>
                {nutrients.length === 0 ? (
                    <Alert variant="warning">No data available</Alert>
                ) : (
                    <ListGroup variant="flush">
                        {nutrients.map((nutrient, index) => (
                            <ListGroup.Item key={index}>
                                <strong>{nutrient.nutrientName}:</strong> {nutrient.totalAmount} {nutrient.nutrientUnitName} ({nutrient.totalPercentDailyValue}% DV)
                            </ListGroup.Item>
                        ))}
                    </ListGroup>
                )}
            </Card.Body>
        </Card>
    );
};

export default Statistics;
