import React from 'react';
import { Card, Row, Col } from 'react-bootstrap';
import { GiFruitBowl, GiCarrot } from 'react-icons/gi'; // Example icons for nutrients

const Statistics = ({ nutrients }) => {
    return (
        <Card className="mb-4" style={{ backgroundColor: '#f3f3f3' }}>
            <Card.Header style={{ backgroundColor: '#64b064', color: 'white' }}>Statistics</Card.Header>
            <Card.Body>
                <Row xs={1} md={2} lg={3} className="g-4">
                    {nutrients.map((nutrient, index) => (
                        <Col key={index}>
                            <Card className="h-100">
                                <Card.Body>
                                    <div className="d-flex align-items-center mb-3">
                                        {nutrient.nutrientName === 'Vitamin C' && <GiFruitBowl style={{ fontSize: '24px', marginRight: '10px' }} />}
                                        {nutrient.nutrientName === 'Vitamin A' && <GiCarrot style={{ fontSize: '24px', marginRight: '10px' }} />}
                                        <div>
                                            <strong>{nutrient.nutrientName}</strong><br />
                                            <span className="text-muted">{nutrient.totalAmount} {nutrient.nutrientUnitName} ({nutrient.totalPercentDailyValue}% DV)</span>
                                        </div>
                                    </div>
                                </Card.Body>
                            </Card>
                        </Col>
                    ))}
                </Row>
            </Card.Body>
        </Card>
    );
};

export default Statistics;
