// components/Statistics.js
import React from 'react';
import { Card, Table } from 'react-bootstrap';

const Statistics = ({ nutrients }) => {
    // Ensure nutrients is always an array
    const nutrientList = Array.isArray(nutrients) ? nutrients : [];

    return (
        <Card className="mb-4">
            <Card.Header style={{ backgroundColor: '#e6ffe6', color: '#006400', fontWeight: 'bold' }}>Statistics</Card.Header>
            <Card.Body>
                <Table striped bordered hover responsive>
                    <thead>
                        <tr>
                            <th>Nutrient</th>
                            <th>Amount</th>
                            <th>Daily %</th>
                        </tr>
                    </thead>
                    <tbody>
                        {nutrientList.map((nutrient, index) => (
                            <tr key={index}>
                                <td>{nutrient.nutrientName}</td>
                                <td>{nutrient.totalAmount} {nutrient.nutrientUnitName}</td>
                                <td>{nutrient.totalPercentDailyValue !== null ? `${nutrient.totalPercentDailyValue}%` : 'N/A'}</td>
                            </tr>
                        ))}
                    </tbody>
                </Table>
            </Card.Body>
        </Card>
    );
};

export default Statistics;
