import React, { useState, useEffect, useContext } from 'react';
import { Form, Button, Table, Badge, ListGroup } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import TokenContext from '../TokenContext';

const AddCustomFood = () => {
    const {getAvailableNutrients, addCustomFood } = useContext(TokenContext);
  const [foodName, setFoodName] = useState('');
  const [availableNutrients, setAvailableNutrients] = useState([
    { id: 1, name: 'Protein', amount: '' },
    { id: 2, name: 'Carbohydrates', amount: '' },
    { id: 3, name: 'Fat', amount: '' },
    { id: 4, name: 'Fiber', amount: '' },
    { id: 5, name: 'Vitamin A', amount: '' },
    { id: 6, name: 'Vitamin C', amount: '' },
    { id: 7, name: 'Calcium', amount: '' },
    { id: 8, name: 'Iron', amount: '' },
    // Add more dummy nutrients as needed
  ]);
  const [filteredNutrients, setFilteredNutrients] = useState([]);
  const [addedNutrients, setAddedNutrients] = useState([]);
  const [errors, setErrors] = useState({});
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    fetchAvailableNutrients();
});




  const fetchAvailableNutrients = async () => {
    const response = await getAvailableNutrients();
    if (response !== null) {
        setAvailableNutrients(response.data);
    }
};

  useEffect(() => {
    // Initialize filtered nutrients with all available nutrients initially
    setFilteredNutrients(availableNutrients);
  }, [availableNutrients]);

  const handleRemoveNutrient = (index) => {
    const updatedNutrients = [...addedNutrients];
    updatedNutrients.splice(index, 1);
    setAddedNutrients(updatedNutrients);
  };

  const handleSaveFood = async(event) => {
    event.preventDefault(); // Prevent form submission
    const formErrors = validateForm();
    if (Object.keys(formErrors).length === 0) {
      // Proceed with saving data or API call
      const newFood = {
        name: foodName,
        nutrients: addedNutrients
      };
      const response= await addCustomFood(newFood);
      if(response!=null)
        {
            console.log('Saving food:', newFood);
            setFoodName('');
            setAddedNutrients([]);
            setErrors({});
            setSearchTerm('');

        }
    } else {
      setErrors(formErrors);
    }
  };

  const validateForm = () => {
    let errors = {};
    if (!foodName.trim()) {
      errors.foodName = 'Food name is required';
    }
    if (addedNutrients.length === 0) {
      errors.nutrients = 'At least one nutrient must be added';
    }
    return errors;
  };

  const handleNutrientSearch = (event) => {
    const searchTerm = event.target.value;
    setSearchTerm(searchTerm);
    console.log("search->",searchTerm)
    console.log("available - >", availableNutrients)
    const filtered = availableNutrients.filter(nutrient =>
      nutrient.name.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setFilteredNutrients(filtered);
    console.log("filtered -> ", filtered)
  };
  const handleNutrientSelect = (nutrient) => {
    // Check if the nutrient is already in the addedNutrients list
    const nutrientExists = addedNutrients.some(addedNutrient => addedNutrient.id === nutrient.id);

    if (!nutrientExists) {
      setAddedNutrients([...addedNutrients, nutrient]);
    }
  };

  return (
    <div className="container mt-5">
      <h2 style={{ color: 'green' }}>Add Custom Food</h2>
      <Form onSubmit={handleSaveFood}>
        <Form.Group controlId="foodName">
          <Form.Label>Food Name</Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter food name"
            value={foodName}
            onChange={(e) => setFoodName(e.target.value)}
            isInvalid={!!errors.foodName}
            required
          />
          <Form.Control.Feedback type="invalid">
            {errors.foodName}
          </Form.Control.Feedback>
        </Form.Group>

        <Form.Group controlId="nutrientSearch">
          <Form.Label>Select Nutrient</Form.Label>
          <Form.Control type="text" placeholder="Search for nutrient..." value={searchTerm} onChange={handleNutrientSearch} />
                <ListGroup className="mb-3">
                    {filteredNutrients.map((nutrient, index) => (
                    <ListGroup.Item
                      key={nutrient.id}
                      action
                     onClick={() => handleNutrientSelect(nutrient)}
                    >
                    {nutrient.name}
                </ListGroup.Item>
            ))}
        </ListGroup>
        </Form.Group>


        { (
          <Table striped bordered hover className="mt-3">
            <thead>
              <tr>
                <th>Nutrient Name</th>
                <th>Amount</th>
                <th></th>
              </tr>
            </thead>
            <tbody>
              {addedNutrients.map((nutrient, index) => (
                <tr key={index}>
                  <td>{nutrient.name}</td>
                  <td>
                    <Form.Control
                      type="number"
                      size="sm"
                      placeholder="Enter amount"
                      value={nutrient.amount || ''}
                      onChange={(e) => {
                        const updatedNutrients = [...addedNutrients];
                        updatedNutrients[index].amount = e.target.value;
                        setAddedNutrients(updatedNutrients);
                      }}
                    />
                  </td>
                  <td>
                    <Button variant="link" onClick={() => handleRemoveNutrient(index)}>
                      <Badge pill bg="danger">
                        X
                      </Badge>
                    </Button>
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>
        )}

        <Button variant="primary" type="submit" className="mt-3">
          Save Food
        </Button>
      </Form>
    </div>
  );
};

export default AddCustomFood;
