import React, { createContext, useState, useEffect } from 'react';
import axios from 'axios';

const TokenContext = createContext();

export const TokenProvider = ({ children }) => {
  const [isLoggedIn,setIsLoggedIn]=useState(false)
  const [token, setToken] = useState(null);
  const [userId,setUserId]=useState(null);
  // const [csrfToken, setCsrfToken] = useState('');

  // useEffect(() => {
  //   axios.get('http://localhost:8080/api/csrf/getCsrfToken').then(response => {
  //     setCsrfToken(response.data.token);
  //     console.log("csrf->",response)
  //   });
  // }, []);


  const logout=() =>{
    setIsLoggedIn(false)
    setToken(null)
  }

  const login = async (userName, password) => {
    let newToken = "Basic " + window.btoa(userName + ':' + password);
    try {
      const response = await axios.get('http://localhost:8080/api/login', {
        headers: {
          Authorization: newToken
        }
      });
      console.log('response', response);
      if (response.status === 200) {
        setUserId(userName)
        setToken(newToken);
        setIsLoggedIn(true);
      }
    } catch (error) {
      console.error('Login failed:', error);
      setToken(null); // Clear token if login fails
      newToken=null
    }
    return newToken;
  };

  const searchByFoodName = async (foodName) => {
    try {
      const response = await axios.get('http://localhost:8080/api/foods/search', {
        params: {
          foodName: foodName
        },
        headers: {
          Authorization: token
        }
      });
      console.log('response -> data -> ', response.data);
      if (response.status === 200) {
        return response;
      }
    } catch (error) {
      console.error('search failed:', error);
      return 0;
    }
  };

  const getFoodNutritionDetails = async (food) => {
    try {
      const response = await axios.get('http://localhost:8080/api/foods/searchFoodNutrientById', {
        params: {
          fdcId: food.fdcId
        },
        headers: {
          Authorization: token
        }
      });
      console.log('response -> data -> ', response.data);
      if (response.status === 200) {
        return response;
      }
    } catch (error) {
      console.error('getting details failed:', error);
      return 0;
    }
  };

  const logFoodNutritionDetails = async (logEntry) => {
    try {
      const response = await axios.post('http://localhost:8080/api/dailyIntake/create',
        logEntry
      , {
        headers: {
          Authorization: token
        }
      });
      console.log('response -> data -> ', response.data);
      if (response.status === 200) {
        return response;
      }
    } catch (error) {
      console.error('logging food nutrition failed:', error);
      return 0;
    }
  };
  return (
    <TokenContext.Provider value={{token, login, isLoggedIn, setIsLoggedIn, logout, searchByFoodName, getFoodNutritionDetails, userId, logFoodNutritionDetails }}>
      {children}
    </TokenContext.Provider>
  );
};

export default TokenContext;
