import React, { useState, useEffect } from 'react';
import { Form, Button, Table } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

const AddCustomFood = () => {
  const [foodName, setFoodName] = useState('');
  const [nutrients, setNutrients] = useState([]);
  const [availableNutrients, setAvailableNutrients] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedNutrient, setSelectedNutrient] = useState('');
  const [nutrientAmount, setNutrientAmount] = useState('');
  const [errors, setErrors] = useState({});

  useEffect(() => {
    // Simulating fetching nutrients from backend
    fetchNutrientsFromBackend()
      .then(data => setAvailableNutrients(data))
      .catch(error => console.error('Error fetching nutrients:', error));
  }, []);

  // Simulated function to fetch nutrients from backend
  const fetchNutrientsFromBackend = () => {
    // Replace with actual fetch logic to your backend API
    return new Promise((resolve, reject) => {
      setTimeout(() => {
        const mockNutrients = [
          { id: 1, name: 'Protein' },
          { id: 2, name: 'Carbohydrates' },
          { id: 3, name: 'Fat' },
          { id: 4, name: 'Fiber' },
          // Add more nutrients as needed
        ];
        resolve(mockNutrients);
      }, 1000); // Simulate delay for API response
    });
  };

  const handleAddNutrient = () => {
    if (selectedNutrient && nutrientAmount) {
      const newNutrient = { name: selectedNutrient, amount: nutrientAmount };
      setNutrients([...nutrients, newNutrient]);
      setSelectedNutrient('');
      setNutrientAmount('');
      setSearchTerm(''); // Reset search term after adding nutrient
    }
  };

  const handleRemoveNutrient = (index) => {
    const updatedNutrients = [...nutrients];
    updatedNutrients.splice(index, 1);
    setNutrients(updatedNutrients);
  };

  const handleSaveFood = (event) => {
    event.preventDefault(); // Prevent form submission
    const formErrors = validateForm();
    if (Object.keys(formErrors).length === 0) {
      // Proceed with saving data or API call
      const newFood = {
        name: foodName,
        nutrients: nutrients
      };
      console.log('Saving food:', newFood);
      // Reset form after saving
      setFoodName('');
      setNutrients([]);
      setSelectedNutrient('');
      setNutrientAmount('');
      setErrors({});
    } else {
      setErrors(formErrors);
    }
  };

  const validateForm = () => {
    let errors = {};
    if (!foodName.trim()) {
      errors.foodName = 'Food name is required';
    }
    if (nutrients.length === 0) {
      errors.nutrients = 'At least one nutrient must be added';
    }
    return errors;
  };

  const handleNutrientSearch = (event) => {
    setSearchTerm(event.target.value);
  };

  const filteredNutrients = availableNutrients.filter(nutrient =>
    nutrient.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="container mt-5">
      <h2 style={{ color: 'green' }}>Add Custom Food</h2>
      <Form onSubmit={handleSaveFood}>
        <Form.Group controlId="foodName">
          <Form.Label>Food Name</Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter food name"
            value={foodName}
            onChange={(e) => setFoodName(e.target.value)}
            isInvalid={!!errors.foodName}
            required
          />
          <Form.Control.Feedback type="invalid">
            {errors.foodName}
          </Form.Control.Feedback>
        </Form.Group>

        <Form.Group controlId="nutrientSearch">
          <Form.Label>Select Nutrient</Form.Label>
          <Form.Control type="text" placeholder="Search for nutrient..." value={searchTerm} onChange={handleNutrientSearch} />
          <Table striped bordered hover className="mt-2">
            <thead>
              <tr>
                <th>Nutrient Name</th>
                <th>Amount</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {nutrients.map((nutrient, index) => (
                <tr key={index}>
                  <td>{nutrient.name}</td>
                  <td>
                    <Form.Control
                      type="text"
                      size="sm"
                      placeholder="Enter amount"
                      value={nutrient.amount}
                      onChange={(e) => {
                        const updatedNutrients = [...nutrients];
                        updatedNutrients[index].amount = e.target.value;
                        setNutrients(updatedNutrients);
                      }}
                    />
                  </td>
                  <td>
                    <Button variant="danger" size="sm" onClick={() => handleRemoveNutrient(index)}>
                      Remove
                    </Button>
                  </td>
                </tr>
              ))}
              {selectedNutrient && (
                <tr>
                  <td>{selectedNutrient}</td>
                  <td>
                    <Form.Control
                      type="text"
                      size="sm"
                      placeholder="Enter amount"
                      value={nutrientAmount}
                      onChange={(e) => setNutrientAmount(e.target.value)}
                    />
                  </td>
                  <td>
                    <Button variant="danger" size="sm" onClick={handleAddNutrient}>
                      Add
                    </Button>
                  </td>
                </tr>
              )}
            </tbody>
          </Table>
        </Form.Group>

        <Button variant="primary" type="submit" className="mt-3">
          Save Food
        </Button>
      </Form>
    </div>
  );
};

export default AddCustomFood;
